
public class MathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Math.min
		System.out.println("min(5,10)= " +Math.min(5, 10));
		//Math.max
		System.out.println("max(5,10)= " +Math.max(5, 10));
		//Math.abs
		System.out.println("abs(-10)= " +Math.abs(-10));
		//Math.pow
		System.out.println("2^10="+Math.pow(2, 10));
		//Math.sqrt
		System.out.println("sqrt(9)="+Math.sqrt(9));
		//Math.ceil
		System.out.println("ceil(7.4)="+Math.ceil(7.4));
		//Math.floor
		System.out.println("floor(7.4)="+Math.floor(7.4));

	}

}
